mapper.select:
SELECT COUNT(1) FROM device AS d LEFT JOIN hospital AS h ON d.`hospital_id` = h.`hospital_id`
d.`hospital_id` = #{hospitalId}
AND d.device_number IN( SELECT device_number FROM device_investor WHERE investor_id = #{userId})

SELECT SUM(ulo.price) FROM device AS d LEFT JOIN hospital AS h ON h.`hospital_id` = d.`hospital_id` INNER JOIN user_lease_order AS ulo ON ulo.device_number = d.`device_number`
d.`hospital_id` = #{hospitalId}
AND d.`device_number` IN(SELECT device_number FROM device_investor WHERE investor_id = #{userId})


where status=2
and investorId=#{investorId}
and device_number in

    #{deviceNumber}
