mapper.select:
SELECT suf.feedback_id,suf.device_number,d.`device_address`, suf.feedback_type,suf.status,suf.feedback_info,suf.feedback_url FROM hospital_user AS hu INNER JOIN hospital AS h ON h.`hospital_id` = hu.`hospital_id` INNER JOIN device AS d ON d.`hospital_id` = h.`hospital_id` INNER JOIN sys_user_feedback AS suf ON suf.device_number = d.`device_number`
hu.`user_name` = #{userId}
and (d.device_address like concat('%',#{deviceNumber},'%') or suf.deviceNumber like concat('%',#{deviceNumber},'%'))

select ifnull(count(DISTINCT(suf.device_number)),0) from hospital_user as hu inner join hospital as h on h.`hospital_id` = hu.`hospital_id` inner join device as d on d.`hospital_id` = h.`hospital_id` inner join sys_user_feedback as suf on suf.device_number = d.`device_number`
hu.`user_name` = #{userId}
and (d.device_address like concat('%',#{deviceNumber},'%') or suf.deviceNumber like concat('%',#{deviceNumber},'%'))
