mapper.update:
update sys_user_feedback
feedback_user_id = #{feedbackUserId}, feedback_describe = #{feedbackDescribe}, device_picture = #{devicePicture}, create_time = #{createTime}, `status` = 1
where feedback_id = #{feedbackId}

mapper.select:
select ifnull(count(DISTINCT(d.device_number)),0) from hospital_user as hu left join hospital as h on h.`hospital_id` = hu.`hospital_id` inner join device as d on d.`hospital_id` = h.`hospital_id` inner join sys_user_feedback as suf on suf.device_number = d.`device_number`
hu.`user_name` = #{userId}
and (d.device_address like concat('%',#{deviceNumber},'%') or d.device_number like concat('%',#{deviceNumber},'%'))

SELECT dt.`device_type_name`,d.device_number,d.`device_address`,d.`status` FROM hospital_user AS hu INNER JOIN device AS d ON d.`hospital_id` = hu.`hospital_id` INNER JOIN device_type AS dt ON dt.`device_type_id` = d.`device_type_id`
hu.`user_name` = #{userId}
AND (d.`device_number` like concat('%',#{deviceNumber},'%') OR d.`device_address` like concat('%',#{deviceNumber},'%'))

SELECT ifnull(count(1),0) FROM hospital_user AS hu INNER JOIN device AS d ON d.`hospital_id` = hu.`hospital_id` INNER JOIN device_type AS dt ON dt.`device_type_id` = d.`device_type_id`
hu.`user_name` = #{userId}
AND (d.`device_number` like concat('%',#{deviceNumber},'%') OR d.`device_address` like concat('%',#{deviceNumber},'%'))
