mapper.select:

SELECT d.device_id, d.device_type_id, d.device_number,d.device_iot_number,d.device_mac,d.user_id,
d.device_deposit, d.billing_id,d.hospital_id, d.device_qR_code, d.device_address,d.content,
d.device_full_address,d.`rows`,d.heart_time, d.create_time, d.status,d.investor_id,
d.electric,d.electric_early,IFNULL(su.`nick_name`,'无') AS agentName,
IFNULL(s.`nick_name`,'无') AS hospitalName FROM device AS d
LEFT JOIN sys_user AS su ON su.`user_id` = d.`user_id`
LEFT JOIN hospital_user AS hu ON hu.`hospital_id` = d.`hospital_id`
LEFT JOIN sys_user AS s ON s.`user_name` = hu.`user_name`
LEFT JOIN device_investor AS di ON di.device_number = d.device_number

     and d.device_type_id = #{deviceTypeId}
     and d.device_number = #{deviceNumber}
     and d.hospital_id = #{hospitalId}
     and d.status = #{status}
     and d.user_id = #{userId}
     and di.investor_id in (#{investorId})

GROUP BY d.device_id

SELECT COUNT(1) FROM device

    (device_number = #{deviceNumber} OR device_mac = #{deviceMac})

        and device_id != #{deviceId}

mapper.update:
update device
device_type_id = #{deviceTypeId}, device_mac = #{deviceMac}, device_iot_number = #{deviceIotNumber}, device_number = #{deviceNumber}, device_deposit = #{deviceDeposit}, billing_id = #{billingId}, hospital_id = #{hospitalId}, device_qR_code = #{deviceQrCode}, device_address = #{deviceAddress}, device_full_address = #{deviceFullAddress}, `rows` = #{rows}, heart_time = #{heartTime}, create_time = #{createTime}, status = #{status}, investor_id = #{investorId}, electric = #{electric}, electric_early = #{electricEarly}, content = #{content}, user_id = #{userId}, investor_proportion = #{investorProportion},
where device_id = #{deviceId}

update device set device_address=#{hospitalAddress},user_id=#{userId},hospital_id=#{hospitalId}
where device_number in

    #{deviceNumber}

update.foreach:
UPDATE device
SET investor_id=#{item.investorId}
WHERE device_number=#{item.deviceNumber}

mapper.update:
update device
device_type_id = #{deviceTypeId}, device_mac = #{deviceMac}, device_iot_number = #{deviceIotNumber}, device_number = #{deviceNumber}, device_deposit = #{deviceDeposit}, billing_id = #{billingId}, hospital_id = #{hospitalId}, device_qR_code = #{deviceQrCode}, device_address = #{deviceAddress}, device_full_address = #{deviceFullAddress}, `rows` = #{rows}, heart_time = #{heartTime}, create_time = #{createTime}, status = #{status}, investor_id = #{investorId}, electric = #{electric}, electric_early = #{electricEarly}, content = #{content}, user_id = #{userId}, investor_proportion = #{investorProportion}, telecom_id = #{telecomId}, masterKey = #{masterKey}, productId = #{productId}, `time` = #{time},
where device_number = #{deviceNumber}

mapper.select:
SELECT (100 - IFNULL(d.`investor_proportion`,0) - IFNULL(su.`proportion`,0)) AS totalProportion FROM device AS d
LEFT JOIN sys_user AS su ON su.`user_id` = d.`user_id`
WHERE d.`device_number` = #{deviceNumber}

SELECT d.device_number FROM hospital_user AS hu
INNER JOIN device AS d ON d.`hospital_id` = hu.`hospital_id`
WHERE hu.user_name = #{userId} AND d.`device_number` = #{deviceNumber}
